package beecrowd;

import java.util.Scanner;

public class HoHoHo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		for (int i = 0; i < n-1; i++) {
			System.out.print("Ho ");
		}
		System.out.println("Ho!");
	}

}
